import psutil
import threading
import time
import base64
from plyer import notification
from flask import Flask
import tempfile
import os
import sys
import ctypes
import winreg  # For adding to Windows startup

app = Flask(__name__)

# --- AUTO MINIMIZE CONSOLE WINDOW ---
def minimize_console():
    try:
        hwnd = ctypes.windll.kernel32.GetConsoleWindow()
        if hwnd:
            SW_MINIMIZE = 6
            ctypes.windll.user32.ShowWindow(hwnd, SW_MINIMIZE)
    except Exception:
        pass

# --- ADD APP TO WINDOWS STARTUP ---
def add_to_startup():
    try:
        app_name = "BatteryMonitorService"
        python_exe = sys.executable.replace("python.exe", "pythonw.exe")  # use pythonw for silent run
        script_path = os.path.abspath(sys.argv[0])
        command = f'"{python_exe}" "{script_path}"'

        key = winreg.OpenKey(
            winreg.HKEY_CURRENT_USER,
            r"Software\Microsoft\Windows\CurrentVersion\Run",
            0,
            winreg.KEY_SET_VALUE,
        )
        winreg.SetValueEx(key, app_name, 0, winreg.REG_SZ, command)
        winreg.CloseKey(key)
        print("✅ Added to startup successfully.")
    except Exception as e:
        print(f"⚠️ Could not add to startup: {e}")

# --- ICON ---
ICON_BASE64 = """

"""

temp_icon_path = tempfile.NamedTemporaryFile(delete=False, suffix=".ico").name
with open(temp_icon_path, "wb") as f:
    f.write(base64.b64decode(ICON_BASE64))

# --- BATTERY CHECK LOOP ---
def check_battery_loop():
    while True:
        battery = psutil.sensors_battery()
        if battery is None:
            notification.notify(
                title="‼️ Unknown Error",
                message="cannot read battery status!",
                timeout=25,
                app_icon=temp_icon_path
            )
            time.sleep(60)
            continue

        percent = battery.percent
        plugged = battery.power_plugged

        if percent <= 40 and not plugged:
            notification.notify(
                title="🔋 Battery Low",
                message="Battery is below 40% and not charging!",
                timeout=25,
                app_icon=temp_icon_path
            )
        elif percent >= 70 and plugged:
            notification.notify(
                title="⚡ Protect Your Battery",
                message=f"Battery is {percent}% charged. Disconnect charger to prolong lifespan.",
                timeout=25,
                app_icon=temp_icon_path
            )

        time.sleep(60)

# --- FLASK SERVER ---
@app.route('/')
def home():
    return "Battery Monitor Service is running in the background."

if __name__ == '__main__':
    minimize_console()
    add_to_startup()

    t = threading.Thread(target=check_battery_loop, daemon=True)
    t.start()

    app.run(host='127.1.2.3', port=4000)
